import React from 'react';
import Calendar from 'react-awesome-calendar';
import styles from '../Styles/Calendar2.css';
import Activity from "../OldComponents/Activity.js";

const BACKEND_ROOT_URL = "https://efeevci-person-site-backend.herokuapp.com";


/*
const events = [{
  id: 1,
  color: '#fd3153',
  from: '2020-11-02T18:00:00+00:00',
  to: '2020-11-07T19:00:00+00:00',
  title: 'Seeing family',
}, {
  id: 2,
  color: '#1ccb9e',
  from: '2020-11-01T13:00:00+00:00',
  to: '2020-11-07T14:00:00+00:00',
  title: 'Holiday',
}, {
  id: 3,
  color: '#F480A8',
  from: '2020-11-05T00:00:00+00:00',
  to: '2020-11-06T00:01:00+00:00',
  title: 'Jet skiing',
}, {
  id: 4,
  color: '#fda256',
  from: '2020-11-05T18:00:00+00:00',
  to: '2020-11-05T19:30:00+00:00',
  title: 'Dinner',
}, {
  id: 5,
  color: '#8281fd',
  from: '2020-11-15T12:00:00+00:00',
  to: '2020-11-15T21:00:00+00:00',
  title: 'Doctors',
}];
*/

class Calendar2 extends React.Component {
  constructor(props) {
    super(props);
    this.calendar = React.createRef();
  }
date = new Date();
current_month = date.getMonth() + 1;
activities = [];
events = [];

getDays(month){
  fetch(BACKEND_ROOT_URL + "/days/" + month).then(res => res.json())
      .then(
          (result) => {
              result.forEach(day => {
                  getActivity(day.day_id, day.date);
              })

          },
          (error) => {
              console.log("Error in getting days: ", error)
          }
      )
}
getActivity(day_id, date) {
  fetch(BACKEND_ROOT_URL + "/activities/" + day_id).then(res => res.json())
      .then(
          (result) => {
              result.forEach(element => {
                  let activity = new Activity();
                  activity.date = date;
                  activity.day_id = day_id;
                  activity.activity_id = element.activity_id;
                  activity.title = element.title;
                  activity.description = element.description;
                  getTag(activity);
                  activities.push(activity);
              });
          },
          (error) => {
              console.log("Error in getting activity: ", error)
          }
      )
}

getTag(activity) {
  fetch(BACKEND_ROOT_URL + "/tags/" + activity.activity_id).then(res => res.json())
      .then(
          (result) => {
              result.forEach(tag => {
                  activity.tags.push(tag.name);
              });
          },
          (error) => {
              console.log("Error in getting tags: ", error)
          }
      )
}

createEvents(activities){
  let id = 0;
 console.log("here");
  for(let activity of activities){
    console.log(activity);
    var ttl = activity.title;
    events.push({
      id : {id},
      color : '#fd3153',
      from : '2021-08-02T18:00:00+00:00',
      to: '2020-08-04T18:00:00+00:00',
      title:{ttl}
    });
  }
}
  componentDidMount() {
    //const details = this.calendar.current.getDetails();
    // call get events endpoint
    getDays(current_month).then(
      () => createEvents(activities).then(
        ()=>console.log("succesfully fetched activities" )
      )
    );

  }

  render() {
    return (
      <div className={styles.pageCalendar}>
        <Calendar
          ref={this.calendar}
          onClickEvent={(event) => console.log('this is an event', event)}
          onChange={(dates) => console.log(dates)}
          onClickTimeLine={(date) => console.log(date)}
          events={events}
        />
      </div>
    );
  }
}export default Calendar2;
